const Div = styled.div`

 display: flex;
 flex-direction: column;
 gap: 110px;
 background-image: url(${bg_image});

 /* nav */

  .nav{
   display: flex;
   justify-content: space-between;
  }

 .nav .rightside, .cart{
      display: flex;
      align-items: center;
    }

    .nav .navicon{
      background: #F4DCC2;
      color: #B00E2F;
      box-shadow: 2px 3px 0px 0px #B00E2F;
    }

    .nav  .menu, .cart{
      border: 1px solid #B00E2F;
    }

    .nav  .cart span{
        border: none;
    }

    .nav  .cart span:nth-child(1){
      border-right: 1px solid #B00E2F;
    }

    .nav  span{
      display: flex;
      justify-content: center;
      align-items: center;
      gap: 1px;
      border: 1px solid #B00E2F;
    }

    .nav  .O_click_menu{
      position: absolute;
      right: 0%;
      top: 0;
      border-bottom-left-radius: 30px;
      width: 95vw;
      height: 130vw;
      overflow: hidden;
      z-index: -1;
      background: transparent;
    }

    .nav  .click_menu{
      position: absolute;
      background: #B00E2F;
      padding: 19.2px 17px 100px 475px;
      right: 0;
      display: flex;
      flex-direction: column;
      border-radius: 0% 0 0% 100%;
      gap: 12vw;
      scale: 0;
      width: 250%;
      height: 180%;
      z-index: 100;
      transform-origin: 100% 0%;
    }

    .nav  .click_menu_top, .click_menu_top_side_1{
      display: flex;
      justify-content: space-between;
    }

    .nav   .click_menu_top_side_2 .navicon, .click_menu_top_side_1 .navicon:nth-child(1){
      background: #B00E2F;
      color: #F4DCC2;
    }

    .nav  .click_menu_top_side_2 .navicon{
      box-shadow: 2px 3px 0px 0px #F4DCC2;
    }

    .nav  .click_menu_top_side_2 .navicon, .click_menu_top_side_1{
      border: 1px solid #F4DCC2;
    }

    .nav   .click_menu_top_side_1 .navicon{
      box-shadow: 2.5px 3px 0px 0px #F4DCC2;
      border: none;
    }

    .nav  li{
      list-style: none;
      font-family: cervo_Medium;
      color: white;
      font-size: 6.5vmax;
      height: 7vmax;
      width: fit-content;
      overflow: hidden;
    }

    .nav  #cross{
      position: relative;
      overflow: hidden;
    }

    .nav  #cross::after{
      position: absolute;
      content: "";
      width: 100%;
      height: 100%;
      background: transparent;
      transform: translateY(0%);
      transition: all 1s ease;
    }

    .nav  #cross.active{
      box-shadow: unset;
      background: #F4DCC2;
      color: #B00E2F;
      transition: all .5s ease;
    }

    .nav  #cross.active::after{
      background: #B00E2F;
      transform: translateY(-100%);
    }

   .nav .menu{
      position: relative;
    }

   .nav .menu::after{
      position: absolute;
      content: "";
      width: 100%;
      height: 100%;
      background: transparent;
      transform: translateY(0%);
      transition: all 1s ease;
    }

   .nav .menu.active::after{
      background: #B00E2F;
      transform: translateY(-100%);
    }

   .nav #cross.active::after{
      background: #B00E2F;
      transform: translateY(-100%);
    }

   .hero{
      display: flex;
      flex-direction: column;
    }

    .hero p{
      font-size: small;
      color: white;
      line-height: 18px;
    }

   .hero .intro_link{
      display: flex;
      align-items: center;
      font-family: cervo_medium;
      color: white;
   }

    .hero .ring{
      position: relative;
      width: fit-content;
      border-radius: 50%;
      overflow: hidden;
    }

    .ring svg:nth-child(3){
      position: absolute;
      animation: unset;
      width: 20px;
      top: 40%;
      left: 40%;
      z-index: 100;
    }

    .ring:hover svg:nth-child(3){
      color: white;
    }

    .ring::after{
      position: absolute;
      content: "";
      width: 100%;
      left: 0;
      border-radius: 50%;
      height: 100%;
      background: white;
      transform: translateY(100%);
      transition: transform .4s ease;
    }

    .ring:hover:after{
      transform: translateY(0%); 
    }

    .ring svg {
      stroke: white;
      animation: rota 8s cubic-bezier(1,1,.12,.12) infinite;
      height: max-content;
    }

    .ring svg:nth-child(2){
      position: absolute;
      left: 0;
      animation-duration: 5s;
    }

    @keyframes rota {
      0%{
        transform: rotate(360deg);
      }
    }

    .hero span{
      display: block;
    }

@media (max-width: 321px){

  height: 100vmax;
  padding: 19.2px 18px;
  background-size: 195%;
  background-position: -160px;
  background-repeat: no-repeat;

/* nav */

  .nav #logo{
    width: 25vw;
    height: 10vw;
  }    
  
  .nav .rightside{
    gap: 4vw;
  }
  
  .nav .navicon{
    width: 11vw;
    height: 11vw;
  }

/* nav */

/* hero */

  .hero{
    gap: 20px;
  }

.hero svg{ 
      width: 192px;
      height: fit-content;
    }

    
   .hero .intro_link{
     gap: 20px;
     margin-top: 10px;
     height: 88px;
  }

  .intro_link span{
    font-size: medium;
    font-weight: 500;
  }

   .hero span:nth-child(2) svg{
      width: 111.5px;
    }

    .hero span:nth-child(3) svg{
      width: 258px;
    }

  #arrow{
    width: 40px;
    transform: translate(65px, 25px);
  }

  .hero .ring{
    height: 88px;
  }

  .ring svg{
    width: 88px;
  }

/* hero */

}

`

